ActorFrame.InitCommand:
%function(self)
gnScreenEnd = false;
if GAMESTATE:GetEnv('Wag') == 'On' then
	self:wag();
	end
end

%function(self)
			self:x(SCREEN_LEFT-58+(HorizontalRecovery()*12));
			self:y(SCREEN_BOTTOM-140);
			self:zoomx(0.9);
			self:zoomy(0.88);
			gnSingleKillP1 = 'no'
			gnDoubleKillP1 = 'no'
			gnTriKillP1 = 'no'
			gnQuadKillP1 = 'no'
			end

BitmapText.InitCommand:
%function(self)
	self:x(SCREEN_CENTER_X-219); self:y(SCREEN_CENTER_Y-219); self:diffusealpha(1); 
	self:zoom(0.5);
	self:shadowlength(0);
	self:horizalign('left');
	if gnLevelP1 == '[Disabled]' then
		self:hidden(1);
		end
		noEXP1 = 0;
		if gnLevelP1 == nil then
			gnLevelP1 = '[Disabled]';
			self:hidden(1);
			end
		self:settext('Level '..gnLevelP1);
	end

%function(self)
	self:x(SCREEN_CENTER_X-219); self:y(SCREEN_CENTER_Y-219); self:diffusealpha(1); 
	self:zoom(0.5);
	self:shadowlength(0);
	self:horizalign('left');
	if gnLevelP1 == '[Disabled]' then
		self:hidden(1);
		end
		noEXP1 = 0;
		if gnLevelP1 == nil then
			gnLevelP1 = '[Disabled]';
			self:hidden(1);
			end
		self:settext('Level '..gnLevelP1);
	end

Layer.InitCommand:
%function(self)
	self:stoptweening();
	self:x(SCREEN_CENTER_X-180); self:y(SCREEN_CENTER_Y-206); self:diffusealpha(1); 
	self:shadowlength(0);
	if gnLevelP1 == '[Disabled]' then
		self:hidden(1);
		else
		self:sleep(0.05);
		self:queuecommand('Init');
		end
	end

%function(self)
	self:stoptweening();
	if gnLevelP1 == '[Disabled]' then
		self:hidden(1);
		end
		if gnPercentageP1 == nil then
			self:stoptweening();
		self:draworder(-1000);
		self:x(SCREEN_CENTER_X-220);
		self:y(SCREEN_CENTER_Y-208);
		self:zoomy(1);
		self:horizalign('left');
			self:sleep(0.1);
			self:queuecommand('Init');
			else
			self:stoptweening();
		self:draworder(-1000);
		self:x(SCREEN_CENTER_X-220);
		self:y(SCREEN_CENTER_Y-208);
		self:zoomy(1);
		self:horizalign('left');
			self:zoomtowidth(gnPercentageP1);
			end
	end

%function(self)
	self:stoptweening();
		self:draworder(-1001);
		self:sleep(0.1);
		self:x(SCREEN_CENTER_X-220);
		self:y(SCREEN_CENTER_Y-208);
		self:zoomy(1);
		self:horizalign('left');
		self:zoomtowidth(93);
	if gnLevelP1 == '[Disabled]' then
		self:hidden(1);
		else
		self:sleep(0.05);
		self:queuecommand('Init');
		end
	end

%function(self)
		self:x(SCREEN_CENTER_X-212);
		self:y(SCREEN_CENTER_Y-194);
		self:zoom(0.85);
		self:diffusealpha(0);
	if gnLevelP1 ~= '[Disabled]' then
		if (gnSinglesSingleP1 == nil) or (gnSinglesDoubleP1 == nil) then
			self:stoptweening();
			self:sleep(0.05);
			self:queuecommand('Init');
			else
			if (gnSinglesSingleP1 == 0) and (gnSinglesDoubleP1 == 0) then
				self:diffusealpha(0);
				else
				self:diffusealpha(1);
				end
			end	
		else
		self:diffusealpha(0);
		end	
	end

BitmapText.InitCommand:
%function(self)
	if gnSinglesSingleP1 == nil then
		self:stoptweening();
		self:sleep(0.05);
		self:queuecommand('Init');
		else
		self:settext(gnSinglesSingleP1);
		end
	self:draworder(10);
	self:x(SCREEN_CENTER_X-212);
	self:y(SCREEN_CENTER_Y-179);
	self:zoom(0.5);
	self:maxwidth(36);
	self:shadowlength(0);
	self:horizalign('center');
	if gnSinglesSingleP1 == 0 then
		self:diffusealpha(0);
		else
		self:diffusealpha(1);
		end
	end

%function(self)
	if gnSinglesDoubleP1 == nil then
		self:stoptweening();
		self:sleep(0.05);
		self:queuecommand('Init');
		else
		self:settext(gnSinglesDoubleP1);
		end
	self:draworder(10);
	self:x(SCREEN_CENTER_X-212);
	self:y(SCREEN_CENTER_Y-179);
	self:zoom(0.5);
	self:maxwidth(36);
	self:shadowlength(0);
	self:horizalign('center');
	if gnSinglesDoubleP1 == 0 then
		self:diffusealpha(0);
		else
		self:diffusealpha(1);
		end
	end

Layer.InitCommand:
%function(self)
		self:x(SCREEN_CENTER_X-187);
		self:y(SCREEN_CENTER_Y-194);
		self:zoom(0.85);
		self:diffusealpha(0);
	if gnLevelP1 ~= '[Disabled]' then
		if (gnDoublesSingleP1 == nil) or (gnDoublesDoubleP1 == nil) then
			self:stoptweening();
			self:sleep(0.05);
			self:queuecommand('Init');
			else
			if (gnDoublesSingleP1 == 0) and (gnDoublesDoubleP1 == 0) then
				self:diffusealpha(0);
				else
				self:diffusealpha(1);
				end
			end	
		else
		self:diffusealpha(0);
		end	
	end

