Layer.UpdateCommand:
%function(self) 
		self:stoptweening();
		if GAMESTATE:GetEnv('Vibrate') == 'On' then
			self:vibrate();
			end
		self:zoom(gnZoom4); 
		self:sleep(0.032);
		self:queuecommand('Update');
		end

%function(self) 
		self:stoptweening();
		if GAMESTATE:GetEnv('Vibrate') == 'On' then
			self:vibrate();
			end
		self:zoom(gnZoom1); 
		self:sleep(0.032);
		self:queuecommand('Update');
		end

%function(self) 
		self:stoptweening();
		if GAMESTATE:GetEnv('Vibrate') == 'On' then
			self:vibrate();
			end
		self:zoom(gnZoom3); 
		self:sleep(0.032);
		self:queuecommand('Update');
		end
