ActorFrame.StartCommand:
%function(self)
		if gnCustomPlayed ~= nil then
			gnCustomPlayed = nil;
			self:hibernate(9999);
			else
						if not getSpecialUSB() then
							self:addx(200000);
							end
			end
					end

Layer.InitCommand:
%function(self)
			self:diffusealpha(0);
			if GAMESTATE:IsHumanPlayer(PLAYER_1) then
				self:x(245+AddFromCenter('x','Tri',PLAYER_1));
				else
				self:x(-64+AddFromCenter('x','Tri',PLAYER_2));
				end
			if GAMESTATE:IsHumanPlayer(PLAYER_1) then self:y(40+AddFromCenter('y','Tri',PLAYER_1)); else  self:y(40+AddFromCenter('y','Tri',PLAYER_2)); end
			self:queuecommand('ColourIt');
			end

Layer.DurationCommand:
%function(self)
				if gnDimBackgroundSeconds == nil then
					gnDimBackgroundSeconds = 0.1;
					end
				if gnDimBackgroundSeconds == 0 then
					gnDimBackgroundSeconds = 0.1;
					end
			self:sleep(gnDimBackgroundSeconds);
			self:linear(1);
			self:diffusealpha(0);
			end

Layer.InitCommand:
%function(self)
			self:diffusealpha(0);
			if GAMESTATE:IsHumanPlayer(PLAYER_1) then
