Layer.InitCommand:
%function(self)
		if gnCustomPlayed ~= nil then
			gnCustomPlayed = nil;
			self:hibernate(9999);
			else
				self:x(-66+AddFromCenter('x','Tri',PLAYER_2));
				self:y(40+AddFromCenter('y','Tri',PLAYER_2));
				self:diffusealpha(0);
				self:zoom(2);
				self:decelerate(0.5);
				self:diffusealpha(gnDiffusealpha);
				self:zoom(gnZoom);
				gnPath = getCustomTriSound();
				SOUND:PlayOnce(gnPath);
						if gnOnScreenSeconds == nil then
							gnOnScreenSeconds = 0.1;
							end
						if gnOnScreenSeconds == 0 then
							gnOnScreenSeconds = 0.1;
							end
				self:sleep(gnOnScreenSeconds);
				self:linear(1);
				self:diffusealpha(0)
				end
			end

%function(self)
				if GAMESTATE:IsHumanPlayer(PLAYER_1) then
					self:x(244+AddFromCenter('x','Tri',PLAYER_1));
					else
					self:x(-66+AddFromCenter('x','Tri',PLAYER_2));
					end
				if GAMESTATE:IsHumanPlayer(PLAYER_1) then self:y(40+AddFromCenter('y','Tri',PLAYER_1)); else  self:y(40+AddFromCenter('y','Tri',PLAYER_2)); end
				self:diffusealpha(0);
				self:zoom(2);
				self:decelerate(0.5);
				self:diffusealpha(gnDiffusealpha);
				self:zoom(gnZoom);
				gnPath = getCustomTriSound();
				SOUND:PlayOnce(gnPath);
						if gnOnScreenSeconds == nil then
							gnOnScreenSeconds = 0.1;
							end
						if gnOnScreenSeconds == 0 then
							gnOnScreenSeconds = 0.1;
							end
				self:sleep(gnOnScreenSeconds);
				self:linear(1);
				self:diffusealpha(0)
			end
